mapper.select:
select

from WZZ_WIFI_SHOP

	
		
			AND ID = #{condition.id,jdbcType=VARCHAR}
		
		
			AND ADDRESS like '%${condition.address}%'
		
		
			AND REMARK like '%${condition.remark}%'
		
		
			AND CLASS_TYPE1 =
			#{condition.classType1,jdbcType=VARCHAR}
		
		
			AND CLASS_TYPE2 =
			#{condition.classType2,jdbcType=VARCHAR}
		
		
			AND WORK_TEL like '%${condition.workTel}%'
		
		
			AND OVER_USER like '%${condition.overUser}%'
		
		
			AND MOBILE_TEL like '%${condition.mobileTel}%'
		
		
			AND WIFI = #{condition.wifi,jdbcType=VARCHAR}
		
		
			AND WIFI_PASSWORD =
			#{condition.wifiPassword,jdbcType=VARCHAR}
		
		
			AND FLOOR = #{condition.floor,jdbcType=VARCHAR}
		
		
			AND WIFI_X = #{condition.wifiX,jdbcType=VARCHAR}
		
		
			AND WIFI_Y = #{condition.wifiY,jdbcType=VARCHAR}
		
		
			AND PROVINCE = #{condition.province,jdbcType=VARCHAR}
		
		
			AND CITY = #{condition.city,jdbcType=VARCHAR}
		
		
			AND WIFI_X >= #{condition.wifiXMin,jdbcType=VARCHAR}
		
		
			AND WIFI_Y >= #{condition.wifiYMin,jdbcType=VARCHAR}
		
		
			AND WIFI_X <= #{condition.wifiXMax,jdbcType=VARCHAR}
		
		
			AND WIFI_Y <= #{condition.wifiYMax,jdbcType=VARCHAR}
